}


export function AGTable({endpointPath}:{endpointPath: string }) {

    const gridRef = useRef<AgGridReact | null>(null);
    const [rowData, setRowData] = useState();
    const [columnDefs, setColumnDefs] = useState<ColDef[]>();

    const defaultColDef = useMemo( () => ({
        sortable: true,
        filter: true,
        resizable: true,
        floatingFilter: true,
    }), []);

    useEffect(() => {
      fetch("127.0.0.1:8000/api/clientes/")
      // fetch('https://www.ag-grid.com/example-assets/row-data.json')
      .then(result => result.json())
      .then(console.log(data));
      // .then(data => {
      //   const colDefs = gridRef.current?.api?.getColumnDefs();
      //   if (colDefs){
      //       colDefs.length=0;
      //       const keys = Object.keys(data[0])
      //       keys.forEach(key => colDefs.push({field : key}));
      //   }
      //   setColumnDefs(colDefs);
      //   setRowData(data);
      //   })
      }, []);


    // Esto es para que el ancho de las columnas de la tabla se actualizen para usar todo el ancho de la ventana.
    const sizeToFit = useCallback(() => {
        gridRef.current?.api?.sizeColumnsToFit({});
    }, []);

    useEffect(() => {
        window.addEventListener('resize', sizeToFit);
        return () => {
        window.removeEventListener('resize', sizeToFit);
        };
    }, []);

    // TODO: HACER QUE sizeToFit se ejecute cuando se carga la ventana por primera vez.
    // Me parece que esto no funciona por c칩mo est치 definido sizeToFit.
    // useEffect(() => {
    //     console.log("ACA DEBERIA FUNCIONAR!")
    //     sizeToFit
    // }, []);

    return (
    <div>
        <div className ='ag-theme-alpine' style={{height: 650}}>
            <AgGridReact 
                ref = {gridRef}
                rowData={rowData}
                columnDefs={columnDefs}
                defaultColDef ={defaultColDef}
                rowSelection='multiple'
                animateRows = {true}
                pagination = {true}
            />
        </div>
    </div>
    )
}
