        <AGTable endpointPath={"http://127.0.0.1:8000/api/pedidos/"} />

        <div className="flex justify-between">
          <div className="flex gap-[8px]">
            <Btn estilo="btnImprimir" valor="AÃ±adir Proveedor" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pedidos;
