import React from "react";
import "./Tabla.css";
import { Btn, BtnIcon } from "./Boton";

function getColumnClass(columna: string) {
  switch (columna) {
    case "id":
      return "id";
    case "Email":
      return "email";
    case "Nombre":
      return "nombre";
    case "Fecha":
      return "fecha";
    case "Plástico":
      return "plastico";
    case "Precio":
      return "precio";
    case "Cantidad(kg)":
      return "cantidad";
    case "Precio Unidad":
      return "precio";
    case "Importe Total":
      return "precio";
    case "Cantidad":
      return "cantidad";
    case "Descripción":
      return "descripcion";
    case "Ultimo Cambio":
      return "fecha";
    case "Nro":
      return "nro";
    case "Total":
      return "total";
    case "Cuotas":
      return "cuotas";
    case "Cliente":
      return "cliente";
    case "Producto":
      return "producto";
    case "Área":
      return "area";
    case "Telefono":
      return "telefono";
    case "Dirección":
      return "direccion";
    case "Fecha de ingreso":
      return "fechaIngreso";
    case "Fecha de registro":
      return "fechaIngreso";
    case "Fecha compra":
      return "fechaIngreso";
    case " ":
      return "accion";
    case "":
      return "accion";
    case "Estado":
      return "estado";
    case "Proveedor":
      return "proveedor";
    default:
      return "";
  }
}

export function Tabla({
  columnas,
  datos,
  estilo,
}: {
  columnas: string[];
  datos: (string | undefined)[][];
  estilo: string;
}) {
  return (
    <table className={estilo}>
      <thead>
        <tr>
          {columnas.map((columna: string, index: number) => (
            <th key={index} className={getColumnClass(columna)}>
              {columna}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {datos ? (
          datos.map((fila: (string | undefined)[], rowIndex: number) => (
            <tr key={rowIndex}>
              {fila.map((dato: string | undefined, cellIndex: number) => (
                <td key={cellIndex}>{dato ?? ""}</td>
              ))}
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={columnas.length}> No data Available </td>
          </tr>
        )}
      </tbody>
    </table>
  );
}

export function TablaEditable({
  columnas,
  datos,
  icono1,
  icono2,
}: {
  columnas: string[];
  datos: (string | undefined)[][];
  icono1: string;
  icono2: string;
}) {
  const handleEdit = (id: string) => {
    // Aquí puedes implementar la lógica para editar los campos de la fila correspondiente al ID
    console.log("Editar fila con ID:", id);
  };

  return (
    <table>
      <thead>
        <tr>
          {columnas.map((columna: string, index: number) => (
            <th key={index} className={getColumnClass(columna)}>
              {columna}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {datos ? (
          datos.map((fila: (string | undefined)[], rowIndex: number) => (
            <tr key={rowIndex}>
              <td>
                <BtnIcon
                  icono={icono1}
                  accion={() => {
                    const element = document.getElementById("show");

                    if (element) {
                      element.style.display = "flex";
                    } else {
                      console.log("no anduvo!");
                    }
                  }}
                  estilo="accion-en-tabla"
                  texto=""
                />
              </td>
              <td>
                <BtnIcon
                  icono={icono2}
                  accion={() => handleEdit(fila[0] ?? "")}
                  estilo="accion-en-tabla"
                  texto=""
                />
              </td>
              {fila.map((dato: string | undefined, cellIndex: number) => (
                <td key={cellIndex}>{dato ?? ""}</td>
              ))}
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={columnas.length}> No data Available </td>
          </tr>
        )}
      </tbody>
    </table>
  );
}

export function Paginado() {
  return (
    <div className="flex justify-between">
      <div className="flex items-center">
        <BtnIcon
          icono="first_page"
          accion={() => {}}
          estilo="paginado-btn"
          texto=""
        />
        <BtnIcon
          icono="chevron_left"
          accion={() => {}}
          estilo="paginado-btn"
          texto=""
        />
        <div className="nroPaginaActual">1</div>
        <BtnIcon
          icono="chevron_right"
          accion={() => {}}
          estilo="paginado-btn"
          texto=""
        />
        <BtnIcon
          icono="last_page"
          accion={() => {}}
          estilo="paginado-btn"
          texto=""
        />
      </div>
    </div>
  );
}

